function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (newValue === '') {
        return;
    }
    var price = g_form.getValue('u_price');
    g_form.clearOptions('u_price');

    var priceAllPieces = g_form.getValue('u_priceallpcs');
    g_form.clearOptions('u_priceallpcs');

    switch (true) {
        case (newValue == 0):
            g_form.addOption('u_price', 0, 'Select Performance of the Car');
            break;
        case (newValue == 1):
            g_form.addOption('u_price', 2, '10');
            // (method) g_form.addOption(fieldName: string, choiceValue: string, choiceLabel: string): void
            break;
        case (newValue == 2):
            g_form.addOption('u_price', 3, '15');
            break;
        case (newValue == 3):
            g_form.addOption('u_price', 4, '20');
            break;
        case (newValue == 4):
            g_form.addOption('u_price', 5, '25');
            break;
        case (newValue == 5):
            g_form.addOption('u_price', 6, '30');
            break;
        case (newValue == 6):
            g_form.addOption('u_price', 7, '35');
            break;
        case (newValue == 7):
            g_form.addOption('u_price', 8, '40');
            break;
        case (newValue == 8):
            g_form.addOption('u_price', 9, '45');
            break;
        case (newValue == 9):
            g_form.addOption('u_price', 10, '50');
            break;
        case (newValue == 10):
            g_form.addOption('u_price', 4, '20');
            break;
        case (newValue == 11):
            g_form.addOption('u_price', 5, '25');
            break;
        case (newValue == 12):
            g_form.addOption('u_price', 6, '30');
            break;
        case (newValue == 13):
            g_form.addOption('u_price', 7, '35');
            break;
        case (newValue == 14):
            g_form.addOption('u_price', 8, '40');
            break;
        case (newValue == 15):
            g_form.addOption('u_price', 10, '50');
            break;
        default:
            alert("none disc performance");
            break;
    }
    var five = 5;
    var pric = g_form.getIntValue('u_price');
    var priceOfOneValue = pric * five;
    var countValue = g_form.getIntValue('u_count');
    var value = priceOfOneValue * countValue;

    g_form.setValue('u_priceallpcs', value);

    // g_form.addErrorMessage('JD priceOfOneValue je ' + priceOfOneValue);
    // g_form.addErrorMessage('JD countValue je ' + countValue);
    // g_form.addErrorMessage('JD five * priceOfOneValue * countValuee je ' + value);
    // g_form.addErrorMessage();

    if (isLoading && !g_form.isNewRecord()) {
        // control, oldValue,  
        g_form.setValue('u_price', price);
        g_form.setValue('u_priceallpcs', priceAllPieces);
    }
}
