function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (newValue === '') {
        return;
    }

    var performance = g_form.getValue('u_performance');
    g_form.clearOptions('u_performance');

    switch (true) {
        case (newValue == 0):
            g_form.addOption('u_performance', 0, 'Select Model of the Car');
            break;
        case (newValue == 11 || newValue == 12):
            g_form.addOption('u_performance', 1, '1.6  (56 kW)');
            g_form.addOption('u_performance', 2, '1.9 TDi ( 62 kW )');
            break;
        case (newValue == 13 || newValue == 14):
            g_form.addOption('u_performance', 1, '1.6  (56 kW)');
            g_form.addOption('u_performance', 2, '1.9 TDi ( 62 kW )');
            break;
        case (newValue == 15):
            g_form.addOption('u_performance', 3, '2.3 TDi (80 kW)');
            g_form.addOption('u_performance', 4, '1.6 JTDM ( 110 kW )');
            g_form.addOption('u_performance', 5, 'JTDM ( 130 kW )');
            break;

        case (newValue == 21 || newValue == 22 || newValue == 23):
            g_form.addOption('u_performance', 7, '2JTS ( 122 kW )');
            g_form.addOption('u_performance', 8, '2.4 JTD ( 130 kW)');
            g_form.addOption('u_performance', 9, '2.6 JTVS (135 kW)');
            break;

        case (newValue == 24 || newValue == 25 || newValue == 26):
            g_form.addOption('u_performance', 10, '2.8 V6 ( 100 kW )');
            g_form.addOption('u_performance', 11, '1.8  MPI (103 kW )');
            break;

        case (newValue == 27):
            g_form.addOption('u_performance', 11, '1.8  MPI (103 kW )');
            break;
        case (newValue == 31 || newValue == 32 || newValue == 33):
            g_form.addOption('u_performance', 10, '2.8 V6 ( 100 kW )');
            g_form.addOption('u_performance', 12, '1.9 JTS ( 118 kW)');
            break;
        case (newValue == 41 || newValue == 42 || newValue == 43):
            g_form.addOption('u_performance', 8, '2.4 JTD ( 130 kW)');
            g_form.addOption('u_performance', 10, '2.8 V6 ( 100 kW )');
            g_form.addOption('u_performance', 12, '1.9 JTS ( 118 kW)');
            break;

        case (newValue == 51 || newValue == 52 || newValue == 53):
            g_form.addOption('u_performance', 3, '2.3 TDi (80 kW)');
            g_form.addOption('u_performance', 5, 'JTDM ( 130 kW )');
            g_form.addOption('u_performance', 7, '2JTS ( 122 kW )');
            break;
        case (newValue == 61 || newValue == 62 || newValue == 63):
            g_form.addOption('u_performance', 12, '1.9 JTS ( 118 kW)');
            g_form.addOption('u_performance', 13, '1.4 TB ( 122 kW)');
            g_form.addOption('u_performance', 14, '1.6 d (89 kW)');
            break;
        default:
            alert("none disc");
            break;
    }

    if (isLoading && !g_form.isNewRecord()) {
        // performance = g_form.getValue('u_performance');
        // control, oldValue,  
        g_form.setValue('u_performance', performance);
    }

}
