sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (newValue === '') {
        return;
    }

    //Type appropriate comment here, and begin script below
    var priceAllPieces = g_form.getValue('u_priceallpcs');
    g_form.clearOptions('u_priceallpcs');

    var five = 5;
    var countValue = g_form.getIntValue('u_count');
    var value = five * newValue * countValue;

    g_form.setValue('u_priceallpcs', value);

    if (isLoading && !g_form.isNewRecord()) {
        g_form.setValue('u_priceallpcs', priceAllPieces);
    }
}
